import React, { Component } from "react";
import Axios from "axios";
import "./login.css";
import logo1 from "../images/logo-saensukcity.png";
// import { Button, Form } from "react-bootstrap";
import { 
  Container, Col, Form,
  FormGroup, Label, Input,
  Button, FormText
} from "reactstrap";

class Login extends Component {
  constructor(props) {
    super(props);

    this.state = {
      username: "",
      password: "",
    };
  }

  onClickLogin = ()=>{
    Axios.post("url", this.state)
  }

  render() {
    return (
      <Container>
        <Form className="bg-transbox">
          <Col>
            <FormGroup>
              <Label>ชื่อผู้ใช้งาน</Label>
              <Input
                type="text"
                name="username"
                placeholder="เลขบัตรประชาชน"
                required
              ></Input>
              <FormText>555555</FormText>
            </FormGroup>
          </Col>
          <Col>
            <FormGroup>
              <Label>รหัสผ่าน</Label>
              <Input
                type="password"
                name="password"
                placeholder="รหัสผ่าน"
                required
              >
              </Input>
            </FormGroup>
          </Col>
          <Button className="mx-auto d-block">ลงชื่อเข้าใช้</Button>
        </Form>
      </Container>
    );
  }
}
export default Login;
