import React, { Component } from "react";
import Axios from "axios";
import "./login.css";
import logo1 from "../images/logo-saensukcity.png";
import { 
  Container,
  Col,
  Form,
  FormGroup,
  Button,
  Card,
  NavLink,
  FormControl} from "react-bootstrap";
// import {
//   Container,
//   Col,
//   Form,
//   FormGroup,
//   Form.Label,
//   Input,
//   Button,
//   Card,
//   NavLink,
// } from "reactstrap";


class Login extends Component {
  render() {
    return (
      <div className="body">
      <Container className="form-position">
        <Card
          className="bg-transbox mx-auto"
          style={{ borderRadius: 30, borderColor: "#707070", minWidth:350 }}
        >
          <br />
          <img className="img-displayed" src={logo1} />
          <Form className="mx-auto" style={{ maxWidth: 400 }}>
            <br />
            <Col>
              <FormGroup>
                <Form.Label>ชื่อผู้ใช้งาน</Form.Label>
                <FormControl
                  type="text"
                  maxLength="13"
                  minLength="13"
                  name="username"
                  placeholder="เลขบัตรประชาชน"
                  // title="ใส่เฉพาะตัวเลยหรือใส่ให้ครบ 13 หลัก"
                  required
                ></FormControl>
              </FormGroup>
            </Col>
            <Col>
              <FormGroup>
                <Form.Label>รหัสผ่าน</Form.Label>
                <FormControl
                  type="password"
                  minLength="8"
                  name="password"
                  placeholder="รหัสผ่าน"
                  // title=""
                  required
                ></FormControl>
                  <NavLink className="forget-pw text-muted" href="#">ลืมรหัวผ่าน?</NavLink><br/>
              </FormGroup>

            </Col>
            <Button
              // onClick={this.onClickValidate}
              color="primary"
              className="mx-auto d-block"
              style={{ borderRadius: 20 }}
            >
              Login
            </Button>
          </Form>
          <p style={{textAlign:"center"}}>MyApp 2020</p>
        </Card>
      </Container>
      </div>
    );
  }
}
export default Login;
