import React, { Component } from "react";
import Axios from "axios";
import "./login.css";
import logo1 from "../images/logo-saensukcity.png";
// import {  } from "react-bootstrap";
import {
  Container,
  Col,
  Form,
  FormGroup,
  Label,
  Input,
  Button,
  Card,
  NavLink,
} from "reactstrap";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faUser } from '@fortawesome/free-solid-svg-icons'
import { icon } from "@fortawesome/fontawesome-svg-core";

class Login extends Component {
  constructor(props) {
    super(props);

    this.state = {
      username: "",
      password: "",
    };
  }

  onClickLogin = () => {
    Axios.post("url", this.state);
  };

  validateid = (e)=>{
    if (this.username=="peter") {
        console.log("no")
    } else {

    }
  }

  render() {
    return (
      <Container className="form-position">
        <Card
          className="bg-transbox mx-auto"
          style={{ borderRadius: 30, borderColor: "#707070", minWidth:350 }}
        >
          <br />
          <img className="img-displayed" src={logo1} />
          <Form className="mx-auto" style={{ maxWidth: 400 }}>
            <br />
            <Col>
              <FormGroup>
                <Label>ชื่อผู้ใช้งาน</Label>
                <FontAwesomeIcon icon={faUser} /><Input
                  onChange={(e)=>{this.setState({ username: e.target.value })}}
                  type="text"
                  maxLength="13"
                  minLength="13"
                  name="username"
                  placeholder="เลขบัตรประชาชน"
                  required
                ><FontAwesomeIcon icon={faUser} /></Input>
              </FormGroup>
            </Col>
            <Col>
              <FormGroup>
                <Label>รหัสผ่าน</Label>
                <Input
                  type="password"
                  minLength="8"
                  name="password"
                  placeholder="รหัสผ่าน"
                  required
                ></Input>
                  <NavLink className="forget-pw text-muted" href="#">ลืมรหัวผ่าน?</NavLink><br/>
              </FormGroup>

            </Col>
            <Button
              onClick={this.validateid}
              color="primary"
              className="mx-auto d-block"
              style={{ borderRadius: 20 }}
            >
              Login
            </Button>
            <p>ddd: {this.username}</p>
          </Form>
          <p style={{textAlign:"center"}}>MyApp 2020</p>
        </Card>
      </Container>
    );
  }
}
export default Login;
